    mutate(
      n = n(),
      rank_drought = rank(obs_mm, ties.method = "average"),
      empirical_rp = (n + 1) / rank_drought
    ) |&gt;
    ungroup() |&gt;
    mutate(source = "OBS") |&gt;
    select(country_aoi, year, window, source, value_mm = obs_mm, empirical_rp)
